import { useRef, useState, useEffect } from 'react';
import p5 from 'p5';
import axios from 'axios';

function App() {
  const [actions, setActions] = useState([]);
  const [color, setColor] = useState(getRandomColor());

  const sketch = (p) => {
    p.setup = () => {
      p.createCanvas(700, 410);
    };

    p.draw = () => {
      if (p.mouseIsPressed) {
        const action = { type: 'draw', x: p.mouseX, y: p.mouseY, color };
        axios.post('http://localhost:8080/actions', action);
        drawAction(p, action);
      }
    };
  };

  const drawAction = (p, action) => {
    p.fill(action.color);
    p.ellipse(action.x, action.y, 20, 20);
  };

  useEffect(() => {
    const fetchActions = async () => {
      const result = await axios.get('http://localhost:8080/actions');
      setActions(result.data);
    };

    const interval = setInterval(fetchActions, 1000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const p5Instance = new p5(sketch, 'container');
    return () => p5Instance.remove();
  }, [sketch]);

  useEffect(() => {
    const p5Instance = new p5(sketch, 'container');
    actions.forEach((action) => drawAction(p5Instance, action));
  }, [actions]);

  const clearBoard = async () => {
    await axios.post('http://localhost:8080/clear');
    setActions([]);
  };

  return (
    <div>
      <button onClick={clearBoard}>Clear</button>
      <div id="container"></div>
    </div>
  );
}

const getRandomColor = () => {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
};

export default App;
